    vellum_post_140 = Post()
    vellum_post_140.title = u'I find running more satisfying in the winter'
    vellum_post_140.slug = u'i-find-running-more-satisfying-in-the-winter'
    vellum_post_140.author = None
    vellum_post_140.markup = u'markdown'
    vellum_post_140.body = u'There\'s still 6" of snow on the ground (more predicted for tonight) and temperatures are hovering around 15-20 degrees Fahrenheit. The only runners on the trail this morning were myself, two women, and a fellow who looked to be in his 70s.\n\nTwo days ago (when the powder was fresh and the snow still falling), it was only me and a couple folks on skis.\n\nWhat\'s with that? Why retreat to central heating when you can generate your own warmth, achieve the satisfying feeling of beating up your body, and be <a href="http://www.youtube.com/watch?v=K2cYWfq--Nw">harder, better, faster, stronger</a> the next day?\n\n(Rinse, repeat.)'
    vellum_post_140.body_rendered = u'<p>There&#8217;s still 6&#8221; of snow on the ground (more predicted for tonight) and temperatures are hovering around 15-20 degrees Fahrenheit. The only runners on the trail this morning were myself, two women, and a fellow who looked to be in his 70s.</p>\n<p>Two days ago (when the powder was fresh and the snow still falling), it was only me and a couple folks on skis.</p>\n<p>What&#8217;s with that? Why retreat to central heating when you can generate your own warmth, achieve the satisfying feeling of beating up your body, and be <a href="http://www.youtube.com/watch?v=K2cYWfq--Nw">harder, better, faster, stronger</a> the next day?</p>\n<p>(Rinse, repeat.)</p>'
